mapper.select:
select *, date_format(regdate, '%Y-%m-%d %T') as fmtdate
from users
where uid=#{uid};

mapper.update:
update users
set uname=#{uname}, phone=#{phone}, address1=#{address1}, address2=#{address2}
where uid=#{uid};

update users
set photo = #{photo}
where uid=#{uid};

update users
set upass = #{upass}
where uid=#{uid};
